mapper.select:
select seq , member_id , title , price , content , address , category , del_yn , soldout_yn , buyer_id , create_date , update_date , delete_date , view_cnt , like_cnt , img1 , img2 , img3 , img4 , img5 , img6 , img7 , img8 , img9 , img10 , latitude , longitude FROM board WHERE 1+1 AND del_yn = 'n'
AND seq = #{seq}
AND soldout_yn = #{soldout_yn}
AND category = #{cateogry}
ORDER BY create_date DESC
LIMIT #{limit}

SELECT a.* FROM ( SELECT seq, member_id, title, price, content, address, category, del_yn, soldout_yn, buyer_id, create_date, update_date, delete_date, view_cnt, like_cnt, img1, img2, img3, img4, img5, img6, img7, img8, img9, img10, latitude AS latitude, longitude AS longitude, ROUND( 6371 * acos( cos(radians(#{userLatitude})) * cos(radians(latitude)) * cos(radians(longitude) - radians(#{userLongitude})) + sin(radians(#{userLatitude})) * sin(radians(latitude)) ), 2 ) AS distance
FROM board ) a WHERE 1 + 1
AND a.del_yn = 'n'
AND soldout_yn = #{soldout_yn}
AND #{distance_from} <= a.distance
AND #{distance_to} >= a.distance
ORDER BY ${order}
${order_desc}
, ${order2}
${order_desc2}
ORDER BY a.create_date DESC
LIMIT #{limit}

SELECT seq, member_id, title, price, content, address, category, del_yn, soldout_yn, buyer_id, create_date, update_date, delete_date, view_cnt, like_cnt, img1, img2, img3, img4, img5, img6, img7, img8, img9, img10, latitude, longitude FROM board WHERE del_yn = 'n'
AND soldout_yn = #{soldout_yn}
AND member_id = #{seller_id}
